import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";

const API_URL = "http://localhost:5001/api/properties"; // ‚úÖ Ensure correct backend URL

const ListProperty = () => {
    const { user } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            alert("‚ùå You must be logged in to access this page.");
            navigate("/login"); // üöÄ Redirects to login page
        }
    }, [user, navigate]);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        price: "",
        location: "",
        bedrooms: "",
        bathrooms: "",
        type: "",
    });

    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user) {
            alert("‚ùå You must be logged in to list a property.");
            return;
        }

        try {
            console.log("üì° Sending property listing request...", formData);

            const response = await axios.post(API_URL, formData, {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });

            console.log("‚úÖ Property listed successfully!", response.data);

            setMessage("‚úÖ Property listed successfully!");
            setTimeout(() => navigate("/properties"), 1500);
        } catch (error) {
            console.error("üö® Error listing property:", error.response?.data || error);
            setMessage(error.response?.data?.error || "‚ùå Failed to list property.");
        }
    };

    return (
        <div className="list-property-container">
            <h2>List Your Property</h2>
            {message && <p className="alert">{message}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" placeholder="Title" value={formData.title} onChange={handleChange} required />
                <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
                <input type="number" name="price" placeholder="Price" value={formData.price} onChange={handleChange} required />
                <input type="text" name="location" placeholder="Location" value={formData.location} onChange={handleChange} required />
                <input type="number" name="bedrooms" placeholder="Bedrooms" value={formData.bedrooms} onChange={handleChange} required />
                <input type="number" name="bathrooms" placeholder="Bathrooms" value={formData.bathrooms} onChange={handleChange} required />
                <select name="type" value={formData.type} onChange={handleChange} required>
                    <option value="">Select Type</option>
                    <option value="apartment">Apartment</option>
                    <option value="house">House</option>
                    <option value="condo">Condo</option>
                </select>
                <button type="submit">List Property</button>
            </form>
        </div>
    );
};

export default ListProperty;
