import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../services/api.js"; // ✅ Import API function
import "../styles/Login.css";

function Login() {
    const [formData, setFormData] = useState({ email: "", password: "" });
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await login(formData); // ✅ Using API function
            if (response.error) {
                setMessage(`<span class="error-message">❌ ${response.error}</span>`); // ❌ Error
            } else {
                localStorage.setItem("token", response.token);
                setMessage(`<span class="success-message">✅ Logged in successfully!</span>`); // ✅ Success
                setTimeout(() => navigate("/properties"), 1500);
            }
        } catch (error) {
            setMessage(`<span class="error-message">❌ Login failed</span>`);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Login</h2>
                {message && <p className="alert">{message}</p>}
                <form onSubmit={handleSubmit}>
                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
                    <button type="submit" className="login-btn">Login</button>
                </form>
                <p className="switch-auth">
                    Don't have an account? <a href="/signup">Sign up</a>
                </p>
            </div>
        </div>
    );
}

export default Login;
