import axios from "axios";

const API_URL = "http://localhost:5001/api";

// âœ… Axios Instance with Global Config
const api = axios.create({
    baseURL: API_URL,
    withCredentials: true, // âœ… Ensures cookies & authentication headers are sent
    headers: { "Content-Type": "application/json" }
});

// âœ… Fetch Properties
export const fetchProperties = async () => {
    try {
        const response = await api.get("/properties"); // âœ… Fixed inconsistent API call
        return response.data;
    } catch (error) {
        console.error("Error fetching properties:", error);
        return [];
    }
};

// âœ… Fetch Single Property by ID
export const fetchPropertyById = async (id) => {
    try {
        const response = await api.get(`/properties/${id}`);
        return response.data;
    } catch (error) {
        console.error("Error fetching property details:", error);
        return null;
    }
};

// âœ… Fix Signup API Route
export const signup = async (userData) => {
    try {
        console.log("ðŸ“¡ Sending signup request:", userData);
        const response = await api.post("/auth/signup", userData); // âœ… Ensured correct endpoint
        console.log("âœ… Signup successful, received response:", response.data);
        return response.data;
    } catch (error) {
        console.error("ðŸš¨ Signup Error:", error.response?.data || error);
        return { error: error.response?.data?.error || "Signup failed" };
    }
};

// âœ… User Login (With Debugging)
export const login = async (credentials) => {
    try {
        console.log("ðŸ“¡ Sending login request:", credentials);
        const response = await api.post("/auth/login", credentials); // âœ… Ensure endpoint is correct
        console.log("âœ… Login successful, received response:", response.data);
        return response.data;
    } catch (error) {
        console.error("ðŸš¨ Error logging in:", error.response?.data || error);
        return { error: error.response?.data?.error || "Login failed" };
    }
};

// âœ… Fetch Logged-in User
export const fetchUser = async (token) => {
    try {
        const response = await api.get("/auth/me", {
            headers: { Authorization: `Bearer ${token}` },
        });
        return response.data;
    } catch (error) {
        console.error("Error fetching user:", error);
        return null;
    }
};

// âœ… Logout
export const logout = async () => {
    try {
        await api.post("/auth/logout");
        localStorage.removeItem("token"); // Remove token
    } catch (error) {
        console.error("Error logging out:", error);
    }
};
