import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../services/api.js"; // ✅ Import API function
import "../styles/Login.css";

function Login() {
    const [formData, setFormData] = useState({ email: "", password: "" });
    const [message, setMessage] = useState("");
    const [messageType, setMessageType] = useState(""); // ✅ Track success or error
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await login(formData); // ✅ Using API function
            if (response.error) {
                setMessage(`❌ ${response.error}`);
                setMessageType("error"); // ✅ Apply error styling
            } else {
                localStorage.setItem("token", response.token);
                setMessage("✅ Logged in successfully!");
                setMessageType("success"); // ✅ Apply success styling

                setTimeout(() => {
                    navigate("/"); // ✅ Redirect to home page
                    window.location.reload(); // ✅ Refresh page after navigation
                }, 1500);
            }
        } catch (error) {
            setMessage("❌ Login failed");
            setMessageType("error");
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Login</h2>
                {message && <p className={`alert ${messageType}`}>{message}</p>} {/* ✅ Dynamic class */}
                <form onSubmit={handleSubmit}>
                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
                    <button type="submit" className="login-btn">Login</button>
                </form>
                <p className="switch-auth">
                    Don't have an account? <a href="/signup">Sign up</a>
                </p>
            </div>
        </div>
    );
}

export default Login;
