import React, {
    createContext,
    useState,
    useEffect,
    useCallback,
    useContext,
} from "react";
import axios from "axios";
import PopupMessage from "../components/PopupMessage.js"; // âœ… Import reusable popup

const API_URL = "http://localhost:5001/api";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [popup, setPopup] = useState({ visible: false, message: "", type: "" });

    // âœ… Fetch logged-in user
    const fetchUser = useCallback(async () => {
        const token = localStorage.getItem("token");
        if (!token) {
            setLoading(false);
            return;
        }

        try {
            const response = await axios.get(`${API_URL}/auth/me`, {
                headers: { Authorization: `Bearer ${token}` },
                withCredentials: true,
            });

            if (response.data) {
                setUser(response.data);
            } else {
                logout();
            }
        } catch (error) {
            console.error("ðŸš¨ Error fetching user:", error.response?.data || error);
            logout();
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchUser();
    }, [fetchUser]);

    // âœ… Login
    const login = async (email, password) => {
        try {
            const response = await axios.post(`${API_URL}/auth/login`, { email, password });

            const { token, user } = response.data;
            if (token) {
                localStorage.setItem("token", token);
                setUser(user);
            } else {
                throw new Error("Token missing in response!");
            }
        } catch (error) {
            console.error("ðŸš¨ Login error:", error.response?.data || error);
            return error.response?.data || { error: "Failed to login" };
        }
    };

    // âœ… Signup
    const signup = async (name, email, password, role) => {
        try {
            const response = await axios.post(`${API_URL}/auth/signup`, {
                name,
                email,
                password,
                role,
            });

            const { token, user } = response.data;
            if (token) {
                localStorage.setItem("token", token);
                setUser(user);
            } else {
                throw new Error("Token missing in response!");
            }
        } catch (error) {
            console.error("ðŸš¨ Signup error:", error.response?.data || error);
            return error.response?.data || { error: "Failed to register" };
        }
    };

    // âœ… Logout with popup and redirect
    const logout = () => {
        localStorage.removeItem("token");
        setUser(null);
        setPopup({
            visible: true,
            message: "âœ… Successfully signed out!",
            type: "success",
        });

        setTimeout(() => {
            setPopup({ visible: false, message: "", type: "" });
            window.location.href = "/login";
        }, 1500);
    };

    return (
        <AuthContext.Provider value={{ user, login, signup, logout, loading }}>
            <>
                {popup.visible && (
                    <PopupMessage message={popup.message} type={popup.type} />
                )}
                {children}
            </>
        </AuthContext.Provider>
    );

};

// âœ… Custom Hook
export const useAuth = () => useContext(AuthContext);

export default AuthContext;
